stdout.script:
{
  function sharedStart(array){
  var A= array.concat().sort(), 
      a1= A[0], a2= A[A.length-1], L= a1.length, i= 0;
  while(i<L && a1.charAt(i)=== a2.charAt(i)) i++;
  return a1.substring(0, i);
  }
  path_list = []
  tmp = [].concat($job.inputs.input_far_junctions)
  tmp.forEach(function(f){return path_list.push(f.path.replace(/\\/g,'/').replace( /.*\//, '' ))})

  common_prefix = sharedStart(path_list)
  return common_prefix.slice(0, common_prefix.length - 3) + "FarJunctions.fa"
}

