fileDef.fileContent:
import json
import subprocess
import sys

job = ""
with open('job.json') as data_file:
    job = json.load(data_file)

if "inputs" in job and "fastq_files" in job["inputs"]:
    fastq_files = job["inputs"]["fastq_files"]
else:
    fastq_files = []

if len(fastq_files)!=2:
    fastq_files = []

output_files = []
for f in fastq_files:

    f = f[0]
    d = {'class': 'File'}

    d['path'] = f["path"]

    if "size" in f:
        d['size'] = f["size"]

    if "contents" in f:
        d['contents'] = f["contents"]

    if "name" in f:
        d['name'] = f["name"]

    if "checksum" in f:
        d['checksum'] = f["checksum"]

    if "location" in f:
        d['location'] = f["location"]

    if "metadata" in f:
        d['metadata'] = f["metadata"]

    if "secondaryFiles" in f:
        d['secondaryFiles'] = f["secondaryFiles"]

    output_files.append(d)

data = {}
if output_files:
    if "metadata" in output_files[0] and "paired_end" in output_files[0]["metadata"] and output_files[0]["metadata"]["paired_end"]=="1":
        data['fastq_file_1'] = output_files[0]
        data['fastq_file_2'] = output_files[1]
    else:
        data['fastq_file_1'] = output_files[1]
        data['fastq_file_2'] = output_files[0]

with open('cwl.output.json', 'w') as w:
    json.dump(data, w)

