        lwr = preds$fit + (1.96 * preds$se.fit)  # ~ higher 95% CI to be conservative
        lwr_2 = preds$fit + 2*(1.96 * preds$se.fit)  # ~ higher 99% CI to be conservative

        anomaly_fusion_reads[, p_predicted:= linearDecoyGLM$family$linkinv(lwr)] # add lower 95% CI prediction
        anomaly_fusion_reads[, p_predicted_2:= linearDecoyGLM$family$linkinv(lwr_2)] # add lower 95% CI prediction

