}

## fusion_reads merging
## JS ADD 8/14
fusion_reads[strand!=0,maxNegStrandOverlap:=max(overlap),by=junction]
fusion_reads[strand==0,maxPosStrandOverlap:=max(overlap),by=junction]
out_fusions=merge(merge(consolidated_fusion_windel, unique(fusion_reads[strand==0,list(junction,maxPosStrandOverlap)]),by="junction",add.x=TRUE), unique(fusion_reads[strand!=0,list(junction,maxNegStrandOverlap)]),by="junction",add.x=TRUE)

out_fusions_left_joint=merge(merge(consolidated_fusion_windel, unique(fusion_reads[strand==0,list(junction,maxPosStrandOverlap)]),by="junction",all.x=TRUE), unique(fusion_reads[strand!=0,list(junction,maxNegStrandOverlap)]),by="junction",all.x=TRUE)

fusionwanomaly_and_indel_juncp_out_left_joint= paste(output_dir,srr,"_FUSION_W_ANOM_AND_INDEL_JUNCPOUT_SPORK_LEFT_JOIN", sep="")

write.table(unique(out_fusions)[order(-junction_cdf.y),], fusionwanomaly_and_indel_juncp_out, row.names=FALSE, quote=FALSE, sep="\t")

write.table(unique(out_fusions_left_joint)[order(-junction_cdf.y),], fusionwanomaly_and_indel_juncp_out_left_joint, row.names=FALSE, quote=FALSE, sep="\t")

cir.verbose=0
