{
  var preset_option = $job.inputs.preset_option
  var alignment_mode = $job.inputs.alignment_mode

  var presets = {
    "Very fast": "--very-fast",
    "Fast": "--fast",
    "Sensitive": "--sensitive",
    "Very sensitive": "--very-sensitive"
  }
  if (alignment_mode == "Local" && preset_option) {
    return presets[preset_option].concat("-local")
  }
  else if (preset_option){
    return presets[preset_option]
  }
}

{
  if ($job.inputs.alignment_mode == "Local") {
    return "--local"
  }
}

{
cmd = ""
reads1 = [];
reads2 = [];
u_reads = [];
reads = [].concat($job.inputs.read_sequence)

if($job.inputs.ignore_paired_end_metadata)
{
	for (var i = 0; i < reads.length; i++){
    	u_reads.push(reads[i].path);
    }
}
else
{

  for (var i = 0; i < reads.length; i++){
      if (reads[i].metadata != null && reads[i].metadata.paired_end == 1){
        reads1.push(reads[i].path);
      }
      else if (reads[i].metadata != null && reads[i].metadata.paired_end == 2){
        reads2.push(reads[i].path);
      }
    else {
      u_reads.push(reads[i].path);
     }
    }
}

if (reads1.length > 0 & reads1.length == reads2.length){
	cmd = "-1 " + reads1.join(",") + " -2 " + reads2.join(",");
}
if (u_reads.length > 0){
    cmd = " -U " + u_reads.join(",");
}


return cmd
}

